package trencadis.infrastructure.services.OntologiesServer.impl.wrapper.xmlOutputGetOntology;/*******************************************************************  *********** CLASE GENERADA AUTOM�TICAMENTE POR XMLWRAPPER **********  ********-----Germ�n Molt� Mart�nez (gmolto@dsic.upv.es) -----*******  ********************************************************************/import org.w3c.dom.*;/** * Contiene los metodos que permiten obtener y cambiar la informacion de los * elementos <STRONG>ONTOLOGY </STRONG> del documento XML. */public class ONTOLOGY extends Wrapper {	/**	 * Crea un Wrapper que trabajara a partir de un elemento ONTOLOGY	 * 	 * @param e El elemento sobre el cual trabaja el Wrapper	 */	public ONTOLOGY(Element e) {		super(e);	}	/**	 * Obtiene el valor del atributo IDOntology	 * 	 * @return El valor del atributo IDOntology	 */	public String getAttr_IDOntology() {		try {			String unparsedValue = elem.getAttribute("IDOntology");			String parsedValue = unparsedValue;			return parsedValue;		} catch (Exception e) {			e.printStackTrace();			return null;		}	}	/**	 * Establece un valor para el atributo IDOntology Si el atributo no existe,	 * lo crea	 * 	 * @param value El valor a introducir en el atributo	 */	public void setAttr_IDOntology(String value) {		try {			String unparsedValue = value;			elem.setAttribute("IDOntology", unparsedValue);		} catch (Exception e) {			e.printStackTrace();		}	}	/**	 * Obtiene el valor del atributo Descripcion	 * 	 * @return El valor del atributo Descripcion	 */	public String getAttr_Description() {		try {			String unparsedValue = elem.getAttribute("Description");			String parsedValue = unparsedValue;			return parsedValue;		} catch (Exception e) {			e.printStackTrace();			return null;		}	}	/**	 * Establece un valor para el atributo Descripcion Si el atributo no existe,	 * lo crea	 * 	 * @param value El valor a introducir en el atributo	 */	public void setAttr_Description(String value) {		try {			String unparsedValue = value;			elem.setAttribute("Description", unparsedValue);		} catch (Exception e) {			e.printStackTrace();		}	}	/**	 * Obtiene el valor del elemento xml_DICOM_SR_Template	 * 	 * @return El valor del elemento xml_DICOM_SR_Template	 */	public String get_xml_DICOM_SR_Template() {		try {			String xml = this.str_to_XML();			int ini = xml.indexOf("<DICOM_SR");			int fin = xml.indexOf("</DICOM_SR>");			xml = xml.substring(ini, fin + 11);			return xml;		} catch (Exception e) {			e.printStackTrace();			return null;		}	}}